export type Abi = Array<FunctionAbi | EventAbi | StructAbi>;
export type AbiEntry = {
    name: string;
    type: 'felt' | 'felt*' | string;
};
declare enum FunctionAbiType {
    'function' = 0,
    'l1_handler' = 1,
    'constructor' = 2
}
export type FunctionAbi = {
    inputs: AbiEntry[];
    name: string;
    outputs: AbiEntry[];
    stateMutability?: 'view';
    state_mutability?: string;
    type: FunctionAbiType;
};
export type AbiStructs = {
    [name: string]: StructAbi;
};
export type StructAbi = {
    members: (AbiEntry & {
        offset: number;
    })[];
    name: string;
    size: number;
    type: 'struct';
};
type EventAbi = any;
export {};
